-- MYSQL语句


-- 登录数据库


mysql -h 127.0.0.1 -u root -p（密码为空可以省略-p）




-- 显示全部数据库


SHOW DATABASES;




-- 创建数据库


CREATE DATABASE IF NOT EXISTS `sunshine` CHARSET utf8 COLLATE utf8_bin;




